import React from 'react'
import './Registration.scss'
import LayoutAuth from '../../hoc/LayoutAuth/LayoutAuth'

class Registration extends React.Component {
	state = {
		roles: ['Выберите должность','Преподаватель','Студент']
	}

	selectRole = () => {
		// отправлять на сервер выбранный элемент	
	}

	renderOptionRole() {
		return this.state.roles.map((item, index) => {
			return (
				<option 
					key={index} 
					onClick={this.selectRole}
				>
					{item}
				</option>
			)
		})
	}

	// componentDidMount() {
		// запрос на сервер для roles
	// }

	render() {
        return (
            <div className='registration'>
				<LayoutAuth
					head='Вход'
					hTitle='Регистрация'
					link='Уже есть аккаунт? Авторизируйтесь!'
					to='/auth'
					img='images/reg.png'
				>
					<div className='all_labels'>
						<label className='label'>Имя пользователя</label><br />
						<label className='label'>Адрес эл. почты</label><br />
						<label className='label'>Должность</label><br />
						{/* <label className='label'>Факультет</label><br /> */}
						{/* <label className='label'>Кафедра</label><br /> */}
						<label className='label'>Пароль</label><br />
						<label className='label'>Введите пароль еще раз</label>
					</div>

					<div className='all_inputs'>
						<input className='input_fields' type='text' /><br />
						<input className='input_fields' type='email' /><br />
						<select className='select' required>
							{ this.renderOptionRole() }
						</select><br />
						{/* <input className='input_fields' type='text' /><br /> */}
						{/* <input className='input_fields' type='text' /><br /> */}
						<input className='input_fields' type='password' /><br />
						<input className='input_fields' type='password' /><br />
						<input type='submit' className='submit input_fields' value='Регистрация пользователя' />
					</div>
				</LayoutAuth>
            </div>
        )
    }
}

export default Registration
