import React from 'react'
import './Forget.scss'
import Layout from '../../hoc/Layout/Layout'
import Label from '../../components/UI/Label/Label'
import Input from '../../components/UI/Input/Input'

class Forget extends React.Component {
    state = {
        fields: [
            { value: '', label: 'Адрес эл. почты', type: 'text', valid: true },
            { value: '', label: 'Телефон', type: 'text', valid: true },
            { value: '', label: 'Номер зачетной книжки', type: 'text', valid: true }
        ]
    }

    onChangeHandler = (event, index) => {
        let fields = [...this.state.fields]
        let control = fields[index]

        control.value = event.target.value
        control.valid = control.value !== ''

        fields[index] = control

        this.setState({
            fields
        })
    }

    // Рендерим колону с label с помощью универсального компонента Label
    renderLabels() {
        return this.state.fields.map((item, index) => {
			return <Label key={index} label={item.label}/>
		})
    }

    // Рендерим колону с input с помощью универсального компонента Input
    renderInputs() {
        return this.state.fields.map((item, index) => {
            return <Input 
                key={index} 
                type={item.type} 
                value={item.value}
                valid={item.valid}
                onChange={event => this.onChangeHandler(event, index)}
                />
		})
    }


	// Layout - компонент высшего порядка для аутентификации
	// (универсальный для регистрации и авторизации)
	// поэтому вносим необходимые данные компонента авторизации в props 
	// и редерим компонент Layout 
    render() {
        return (
            <Layout
				head='Вход'
				hTitle='Восстановление пароля'
                link='Вернуться'
                to='/auth'
                img='images/user.png'
			>
                <div className='all_labels'>
                    { this.renderLabels() }
                </div>

                <div className='all_inputs'>
                    { this.renderInputs() }<br />

                    <input className='submit any_types_inputs' type='submit' value='Восстановление' />
                </div>
			</Layout>
        )
    }
}

export default Forget
