import React from 'react'

const Input = props => {
    const cls = ['input_fields']

    !props.valid ? cls.push('invalid') : null

    return (
        <input
            className={cls.join(' ')}
            type={props.type}
            value={props.value}
            onChange={props.onChange}
        />
    )
}

export default Input
